import { useCallback } from "react";
import Particles from "react-tsparticles";
import { loadSlim } from "tsparticles-slim";

export function Background() {
  const particlesInit = useCallback(async (engine) => {
    console.log("ðŸŸ¢ Inicializando partÃ­culas (NeÃ³n QA Edition)...");
    await loadSlim(engine);
  }, []);

  const particlesLoaded = useCallback((container) => {
    console.log("âœ… PartÃ­culas cargadas correctamente:", container);
  }, []);

  return (
    <Particles
      id="tsparticles"
      init={particlesInit}
      loaded={particlesLoaded}
      style={{
        position: "fixed",
        top: 0,
        left: 0,
        width: "100vw",
        height: "100vh",
        zIndex: 0,
        pointerEvents: "none",
      }}
      options={{
        background: {
          color: { value: "transparent" },
        },
        fpsLimit: 60,
        detectRetina: true,
        fullScreen: { enable: false },
        particles: {
          number: {
            value: 120,
            density: { enable: true, area: 800 },
          },
          color: {
            value: ["#db2777", "#7e22ce", "#06b6d4"],
          },
          shape: {
            type: "circle",
          },
          opacity: {
            value: 0.7,
            random: true,
            animation: {
              enable: true,
              speed: 1,
              minimumValue: 0.2,
            },
          },
          size: {
            value: { min: 1, max: 3 },
            animation: {
              enable: true,
              speed: 2,
              minimumValue: 0.3,
            },
          },
          links: {
            enable: true,
            distance: 150,
            color: "#db2777",
            opacity: 0.5,
            width: 1.2,
            shadow: {
              enable: true,
              blur: 5,
              color: "#db2777",
            },
          },
          move: {
            enable: true,
            speed: 1.2,
            direction: "none",
            random: true,
            straight: false,
            outModes: { default: "out" },
            attract: { enable: false },
          },
        },
        interactivity: {
          events: {
            onHover: { enable: true, mode: "repulse" },
            onClick: { enable: true, mode: "push" },
          },
          modes: {
            repulse: { distance: 120, duration: 0.5 },
            push: { quantity: 4 },
          },
        },
      }}
    />
  );
}
