import { useState, useEffect } from "react";
import { FaGithub, FaLinkedin, FaTimes } from "react-icons/fa";
import { motion, AnimatePresence } from "framer-motion";

export function Navbar() {
  const [active, setActive] = useState("home");
  const [scrolled, setScrolled] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);

  // 🔹 Detectar sección activa y scroll
  useEffect(() => {
    const handleScroll = () => {
      const sections = document.querySelectorAll("section[id]");
      let current = "";
      sections.forEach((section) => {
        const sectionTop = section.offsetTop - 120;
        const sectionHeight = section.offsetHeight;
        if (
          window.scrollY >= sectionTop &&
          window.scrollY < sectionTop + sectionHeight
        ) {
          current = section.getAttribute("id");
        }
      });
      setActive(current);
      setScrolled(window.scrollY > 80);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const menuItems = [
    { id: "home", label: "INICIO" },
    { id: "about", label: "SOBRE MÍ" },
    { id: "skills", label: "HABILIDADES" },
    { id: "projects", label: "PROYECTOS" },
    { id: "contact", label: "CONTACTO" },
  ];

  return (
    <header
      className={`fixed top-0 left-0 w-full z-50 transition-all duration-500 ${
        scrolled
          ? "bg-black/40 backdrop-blur-md shadow-md border-b border-gray-800"
          : "bg-transparent"
      }`}
    >
      <nav className="max-w-6xl mx-auto flex justify-between items-center h-20 px-6">
        {/* 🔹 Logo */}
        <h1 className="text-2xl md:text-3xl font-bold tracking-wider text-indigo-200">
          Luis Veliz
        </h1>

        {/* 🔹 Menú desktop */}
        <ul className="hidden md:flex gap-10 bg-black/30 px-10 py-3 rounded-2xl items-center border border-gray-700 backdrop-blur-md shadow-[0_0_15px_rgba(191,219,254,0.2)]">
          {menuItems.map((link) => (
            <li key={link.id}>
              <a
                href={`#${link.id}`}
                className={`text-sm font-semibold tracking-widest uppercase transition-all duration-300 ${
                  active === link.id
                    ? "text-cyan-400 drop-shadow-[0_0_6px_rgba(34,211,238,0.8)]"
                    : "text-gray-300 hover:text-cyan-300"
                }`}
              >
                {link.label}
              </a>
            </li>
          ))}
        </ul>

        {/* 🔹 Redes */}
        <div className="hidden md:flex gap-4">
          <a
            href="https://github.com/Rudeluy"
            target="_blank"
            rel="noreferrer"
            className="text-gray-300 hover:text-white transition"
          >
            <FaGithub size={22} />
          </a>
          <a
            href="https://www.linkedin.com/in/luis-alfredo-veliz/"
            target="_blank"
            rel="noreferrer"
            className="text-gray-300 hover:text-white transition"
          >
            <FaLinkedin size={22} />
          </a>
        </div>

        {/* 🔹 Menú móvil */}
        <div
          className="md:hidden text-gray-300 text-2xl cursor-pointer"
          onClick={() => setMenuOpen(!menuOpen)}
        >
          {menuOpen ? <FaTimes /> : "☰"}
        </div>
      </nav>

      {/* 🔹 Menú desplegable móvil */}
      <AnimatePresence>
        {menuOpen && (
          <motion.div
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            transition={{ duration: 0.3 }}
            className="md:hidden absolute top-20 left-0 w-full bg-black/90 backdrop-blur-lg border-t border-gray-700 shadow-lg z-40"
          >
            <ul className="flex flex-col items-center gap-6 py-6">
              {menuItems.map((link) => (
                <li key={link.id}>
                  <a
                    href={`#${link.id}`}
                    onClick={() => setMenuOpen(false)}
                    className={`text-lg font-semibold uppercase tracking-wide ${
                      active === link.id
                        ? "text-cyan-400"
                        : "text-gray-200 hover:text-cyan-300"
                    }`}
                  >
                    {link.label}
                  </a>
                </li>
              ))}
            </ul>
          </motion.div>
        )}
      </AnimatePresence>
    </header>
  );
}
