import { useCallback } from "react";
import Particles from "react-tsparticles";
import { loadSlim } from "tsparticles-slim";

export function Background() {
  const particlesInit = useCallback(async (engine) => {
    console.log("ðŸŸ¢ Inicializando partÃ­culas (QA NeÃ³n Edition)");
    await loadSlim(engine);
  }, []);

  const particlesLoaded = useCallback((container) => {
    console.log("âœ… PartÃ­culas cargadas correctamente:", container);
    // Forzar render en DOM
    if (container?.canvas?.element) {
      container.canvas.element.style.position = "fixed";
      container.canvas.element.style.top = "0";
      container.canvas.element.style.left = "0";
      container.canvas.element.style.width = "100vw";
      container.canvas.element.style.height = "100vh";
      container.canvas.element.style.zIndex = "0";
      container.canvas.element.style.pointerEvents = "none";
    }
  }, []);

  return (
    <div
      className="absolute inset-0 -z-10"
      style={{
        position: "fixed",
        top: 0,
        left: 0,
        width: "100vw",
        height: "100vh",
        zIndex: 0,
        overflow: "hidden",
      }}
    >
      <Particles
        id="tsparticles"
        init={particlesInit}
        loaded={particlesLoaded}
        options={{
          background: { color: { value: "transparent" } },
          fullScreen: { enable: false },
          detectRetina: true,
          fpsLimit: 60,
          particles: {
            number: { value: 100, density: { enable: true, area: 800 } },
            color: { value: ["#db2777", "#7e22ce", "#06b6d4"] },
            shape: { type: "circle" },
            opacity: {
              value: 0.7,
              random: true,
              animation: { enable: true, speed: 1, minimumValue: 0.2 },
            },
            size: {
              value: { min: 1, max: 3 },
              animation: { enable: true, speed: 2, minimumValue: 0.3 },
            },
            links: {
              enable: true,
              distance: 150,
              color: "#db2777",
              opacity: 0.4,
              width: 1.2,
            },
            move: {
              enable: true,
              speed: 1.1,
              outModes: { default: "out" },
            },
          },
          interactivity: {
            events: {
              onHover: { enable: true, mode: "repulse" },
              onClick: { enable: true, mode: "push" },
            },
            modes: {
              repulse: { distance: 120, duration: 0.5 },
              push: { quantity: 4 },
            },
          },
        }}
      />
    </div>
  );
}
