import { motion } from "framer-motion";

export function Testimonials() {
  const testimonials = [
    {
      quote:
        "Luis demostró gran compromiso y atención al detalle en todas las fases del proyecto OneView.",
      author: "Marcela Sánchez · Líder Scrum, Imagemaker",
    },
    {
      quote:
        "Excelente colaboración y comunicación, aportando mejoras continuas a los procesos QA.",
      author: "Paula Carvajal · Jefa de Proyecto, Microdat",
    },
  ];

  return (
    <section id="testimonials" className="bg-gray-900 py-24 px-6 md:px-20">
      <div className="max-w-5xl mx-auto text-center">
        <h2 className="text-4xl font-bold text-pink-400 mb-12">Testimonios</h2>
        <div className="space-y-10">
          {testimonials.map((t, i) => (
            <motion.div
              key={i}
              initial={{ opacity: 0, y: 40 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, ease: "easeOut" }}
              className="bg-gray-800 p-8 rounded-xl shadow-md border border-gray-700 hover:border-indigo-500 transition"
            >
              <p className="text-gray-300 italic mb-4">“{t.quote}”</p>
              <p className="text-indigo-400 font-semibold">{t.author}</p>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
}
