import { useCallback } from "react";
import Particles from "react-tsparticles";
import { loadSlim } from "tsparticles-slim";

export function Background() {
  const particlesInit = useCallback(async (engine) => {
    console.log("ðŸŸ¢ Inicializando partÃ­culas...");
    await loadSlim(engine); // Carga liviana y moderna
  }, []);

  const particlesLoaded = useCallback((container) => {
    console.log("âœ… PartÃ­culas cargadas correctamente:", container);
  }, []);

  return (
    <Particles
      id="tsparticles"
      init={particlesInit}
      loaded={particlesLoaded}
      style={{
        position: "fixed",
        top: 0,
        left: 0,
        width: "100%",
        height: "100%",
        zIndex: 0,
        pointerEvents: "none",
      }}
      options={{
        background: { color: { value: "transparent" } },
        fpsLimit: 60,
        detectRetina: true,
        fullScreen: { enable: false },
        particles: {
          number: { value: 80, density: { enable: true, area: 800 } },
          color: { value: ["#db2777", "#7e22ce", "#06b6d4"] },
          shape: { type: "circle" },
          opacity: { value: 0.6 },
          size: { value: { min: 1, max: 3 } },
          links: {
            enable: true,
            distance: 130,
            color: "#ec4899",
            opacity: 0.4,
            width: 1,
          },
          move: {
            enable: true,
            speed: 0.8,
            direction: "none",
            outModes: { default: "out" },
          },
        },
        interactivity: {
          events: {
            onHover: { enable: true, mode: "repulse" },
            onClick: { enable: true, mode: "push" },
          },
          modes: {
            repulse: { distance: 120 },
            push: { quantity: 4 },
          },
        },
      }}
    />
  );
}
