import { Navbar } from "./components/Navbar";
import { Background } from "./components/Background.jsx";
import { Hero } from "./components/Hero";
import { About } from "./components/About";
import { Skills } from "./components/Skills";
import { Projects } from "./components/Projects";
import { Testimonials } from "./components/Testimonials";
import { Contact } from "./components/Contact";
import { Footer } from "./components/Footer";

function App() {
  return (
    <div className="text-white font-sans scroll-smooth relative overflow-hidden bg-transparent">      {/* ðŸ”¹ Navbar fijo */}
      <Navbar />

      {/* ðŸ”¹ Fondo animado de partÃ­culas detrÃ¡s del Hero */}
      <div className="absolute inset-0 -z-10">
        <Background />
      </div>

      {/* ðŸ”¹ Contenido principal */}
      <main className="pt-24 space-y-0">
        {/* ðŸŸ£ Hero */}
        <section
          id="home"
          className="min-h-screen flex justify-center items-center text-center bg-gradient-to-br from-darker via-dark to-black scroll-mt-24"
        >
          <Hero />
        </section>

        {/* ðŸ”µ About */}
        <section id="about" className="py-32 bg-dark scroll-mt-24">
          <About />
        </section>

        {/* ðŸŸ£ Skills */}
        <section id="skills" className="py-32 bg-darker scroll-mt-24">
          <Skills />
        </section>

        {/* ðŸ”µ Projects */}
        <section id="projects" className="py-32 bg-dark scroll-mt-24">
          <Projects />
        </section>

        {/* ðŸŸ£ Testimonials */}
        <section id="testimonials" className="py-32 bg-darker scroll-mt-24">
          <Testimonials />
        </section>

        {/* ðŸ”µ Contact */}
        <section id="contact" className="py-32 bg-dark scroll-mt-24">
          <Contact />
        </section>
      </main>

      {/* ðŸ”¹ Footer */}
      <Footer />
    </div>
  );
}

export default App;
