import Particles from "react-tsparticles";
import { loadFull } from "tsparticles";

export function Background() {
  const particlesInit = async (engine) => {
    await loadFull(engine);
  };

  return (
    <div className="fixed top-0 left-0 w-full h-full -z-10">
      <Particles
        id="tsparticles"
        init={particlesInit}
        options={{
          background: {
            color: "transparent",
          },
          fullScreen: { enable: false }, // desactiva fullscreen para evitar sobreposición con Tailwind
          fpsLimit: 60,
          particles: {
            number: {
              value: 70,
              density: { enable: true, area: 800 },
            },
            color: {
              value: ["#db2777", "#06b6d4", "#7e22ce"],
            },
            links: {
              enable: true,
              distance: 120,
              color: "#ec4899",
              opacity: 0.6,
              width: 1,
            },
            move: {
              enable: true,
              speed: 1,
              direction: "none",
              random: true,
              straight: false,
              outModes: "out",
            },
            opacity: { value: 0.5 },
            size: { value: { min: 1, max: 3 } },
          },
          interactivity: {
            events: {
              onHover: { enable: true, mode: "repulse" },
              onClick: { enable: true, mode: "push" },
            },
            modes: {
              repulse: { distance: 100 },
              push: { quantity: 3 },
            },
          },
          detectRetina: true,
        }}
      />
    </div>
  );
}
